const { calculateFinalPrice, getPriceBreakdown } = require('./app/utils/pricing');

function testPricingFunctions() {
    const testCases = [
        { basePrice: 30, expectedFee: 8, expectedTotal: 38 },
        { basePrice: 75, expectedFee: 12, expectedTotal: 87 },
        { basePrice: 150, expectedFee: 20, expectedTotal: 170 },
        { basePrice: 250, expectedFee: 50, expectedTotal: 300 },
    ];

    testCases.forEach(({ basePrice, expectedFee, expectedTotal }) => {
        const finalPrice = calculateFinalPrice(basePrice);
        const breakdown = getPriceBreakdown(basePrice);

        console.log(`Base Price: $${basePrice}`);
        console.log(`Expected Fee: $${expectedFee}, Calculated Fee: $${breakdown.fee}`);
        console.log(`Expected Total: $${expectedTotal}, Calculated Total: $${breakdown.total}`);
        console.log('---');
    });
}

testPricingFunctions();

